import React from 'react';
import './FeedbackForm.css';

class FeedbackForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {isToggleOn: true};

    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('Отправленное имя: ' + this.state.value);
    event.preventDefault();
  }
  handleClick() {
    this.setState(prevState => ({
      isToggleOn: !prevState.isToggleOn
    }));
    console.log(this.state.isToggleOn);

  }
  render(){
    return (
      <form onSubmit={this.handleSubmit}>
        <div className='container m-2'>
            <h1>{this.state.isToggleOn ? 'First type of header' : 'Second type of header(adding diff text)'}</h1>
            <div className='d-flex justify-content-center'>
              <button className='btn btn-danger' onClick={this.handleClick}>Press to change header</button>
            </div>
        </div>
        <div className='container m-2'>
          <label>
            Имя:
            <input className={this.state.value == null? 'border border-danger': 'border border-primary'} type="text" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Отправить" />
        </div>
      </form>

    );
  }

}

export default FeedbackForm;

